    struct SBinaryUintOp {
        uint input1;
        uint input2;
        uint output1; 
        uint output2;
    };
    RWStructuredBuffer<SBinaryUintOp> g_buf : register(u0);
    [numthreads(8,8,1)]
    void main(uint GI : SV_GroupIndex) {
        SBinaryUintOp l = g_buf[GI];
        uint2 x = uint2(l.input1, l.input2);
        uint2 y = AddUint64(x, x);
        l.output1 = y.x;
        l.output2 = y.y;
        g_buf[GI] = l;
    };
