    PerThreadData pts = g_sb[GI];
    pts.firstLaneId = WaveReadLaneFirst(GI);
    pts.laneIndex = WaveGetLaneIndex();
    if (pts.mask != 0) {
        pts.output = WaveActiveBitAnd(pts.input);
    }
    else {
        pts.output = WaveActiveBitAnd(pts.input);
    }
    g_sb[GI] = pts;
}


